function gcc\(n\){if\(!n\)return'TR';const l=n.trim\(\).toLowerCase\(\);if\(l.includes\('rk'\)\)return'TR';if\(CC[l]\)return CC[l];for\(const[k,v]of Object.entries\(CC\)\){if\(l.includes\(k\)||k.includes\(l\)\)return v;}return l.substring\(0,2\).toUpperCase\(\);}
