function sn\(f\){const p=f.trim\(\).split\(/\\s+/\);if\(!p[0]\)return['',''];if\(p.length===1\)return['',p[0]];return[p[0],p.slice\(1\).join\(' '\)];}
function dec\(s\){return s.replace\(/&amp;/g,'&'\).replace\(/&quot;/g,'"'\).replace\(/&#039;/g,"'"\).replace\(/&nbsp;/g,' '\);}
function parse\(html\){
  const players=[];
  const re=/class="zentriert rueckennummer bg_\(\\w+\)" title="\([^"]*\)"/g;
  const indices=[];
  let m;
  while\(\(m=re.exec\(html\)\)!==null\){indices.push\({idx:m.index,end:m.index+m[0].length,pos:m[2]}\);}
  for\(let i=0;i<indices.length;i++\){
    const seg=html.substring\(indices[i].end,i+1<indices.length?indices[i+1].idx:html.length\);
    const sm=seg.match\(/<div class=rn_nummer>\(\\d+\)<\\/div>/\);
    const shirt=sm?parseInt\(sm[1]\):0;
    const pm=seg.match\(/\\/profil\\/spieler\\/\(\\d+\)"[^>]*>\([\\s\\S]*?\)<\\/a>/\);
    if\(!pm\)continue;
    const tmId=pm[1];
    let name=dec\(pm[2].replace\(/<[^>]*>/g,''\).trim\(\)\);
    const hm=seg.match\(/class="hauptlink"[^>]*>\\s*<a[^>]+>\([\\s\\S]*?\)<\\/a>/\);
    if\(hm\){const c=dec\(hm[1].replace\(/<[^>]*>/g,''\).trim\(\)\);if\(c\)name=c;}
    const am=seg.match\(/\\d{2}\\/\\d{2}\\/\\d{4}\\s*\\\(\(\\d+\)\\\)/\);
    const age=am?parseInt\(am[1]\):0;
    const nm=seg.match\(/title="\([^"]+\)"[^>]*class="flaggenrahmen"/\);
    const nat=nm?gcc\(nm[1]\):'TR';
